import SwiftUI
let pets = [
    Pet(name: "Poodle", diet: .carnivorous, petPhotos: [], petImage: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTmnueMINKqoyf835Alf4XNZb1UMmCdeVx7jA&s"),
    Pet(name:"Husky", diet: .carnivorous, petPhotos: [], petImage:
       "data:image/jpeg;base64,"),
    Pet(name: "Rex", diet: .carnivorous, petPhotos: [], petImage: "")
]
struct PetRow: View {
    var pet: Pet
    var body: some View {
        HStack {
            AsyncImage(url: URL(string: pet.petImage),content: { petImage in petImage
                    .resizable()
                .aspectRatio(contentMode: .fit)}, placeholder: {ProgressView()})
            Text(pet.name)
                .font(.system(size:20, weight: .bold))
        }
    }
}

struct PetView: View {
    var body: some View {
        List(pets) {pet in
            PetRow(pet: pet)
        }
    }
}

#Preview {
    PetView()
